function hexToBinary(hexString) {
    // Parse the hexadecimal string to an integer
    const intValue = parseInt(hexString, 16);

    // Convert the integer to a binary string
    let binaryString = intValue.toString(2);

    // Pad the binary string with leading zeros if necessary
    // Assuming each hex digit should be represented by 4 binary digits
    const hexLength = hexString.length;
    const expectedBinaryLength = hexLength * 4;
    while (binaryString.length < expectedBinaryLength) {
        binaryString = '0'.concat(binaryString);
    }

    return binaryString;
}

function decimalToReversedBinary(decimal) {
    // Convert the decimal number to a binary string
    let binaryString = decimal.toString(2);

    // Pad the binary string with leading zeros to ensure it has a length of 4 bits
    while (binaryString.length < 4) {
        binaryString = '0'.concat(binaryString);
    }

    // Reverse the binary string
    const reversedBinaryString = binaryString.split('').reverse().join('');

    return reversedBinaryString;
}

// Definisikan parameter PID
const Kp = 0.5; // Gain proporsional
const Ki = 0.2; // Gain integral
const Kd = 0.1; // Gain derivatif

let errorPrev = 0;
let integral = 0;

// Fungsi untuk mengatur heater dengan kontrol PID
function kontrolPID(nilaiTarget, nilaiSekarang) {
    // Hitung error saat ini
    const error = nilaiTarget - nilaiSekarang;

    // Hitung komponen PID
    const proporsional = Kp * error;
    integral += error;
    const integralComp = Ki * integral;
    const derivative = Kd * (error - errorPrev);

    // Hitung output PID
    const output = proporsional + integralComp + derivative;

    // Simpan error saat ini untuk penggunaan di iterasi selanjutnya
    errorPrev = error;

    return output;
}

