function decToBinary(dec) {
    // Ensure the input is a number
    if (typeof dec !== 'number' || isNaN(dec)) {
        throw new Error('Input must be a valid number');
    }

    // Handle negative numbers by using two's complement representation
    if (dec < 0) {
        return (dec >>> 0).toString(2).padStart(32, '0');
    }

    // Convert decimal to binary using toString method
    let binaryStr = dec.toString(2);

    // Pad the binary string to 8 bits
    return binaryStr.padStart(8, '0');
}
