function secondsToHumanReadable(seconds) {
    const units = [
        { label: 'year', value: 60 * 60 * 24 * 365 },  // Approximate, not accounting for leap years
        { label: 'month', value: 60 * 60 * 24 * 30 },  // Approximate
        { label: 'day', value: 60 * 60 * 24 },
        { label: 'hour', value: 60 * 60 },
        { label: 'minute', value: 60 },
        { label: 'second', value: 1 }
    ];

    const result = [];

    for (const unit of units) {
        const unitValue = Math.floor(seconds / unit.value);
        if (unitValue > 0) {
            result.push(`${unitValue} ${unit.label}${unitValue > 1 ? 's' : ''}`);
            seconds -= unitValue * unit.value;
        }
    }

    return result.length > 0 ? result.join(' ') : '0 seconds';
}

